function burgerMenu(){const e=document.querySelector(".burger"),i=document.querySelector(".menu"),r=document.querySelector("body");e.addEventListener("click",(()=>{i.classList.contains("active")?(i.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked")):(i.classList.add("active"),e.classList.add("active-burger"),r.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((s=>{s.addEventListener("click",(function(){i.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(i.classList.remove("active"),e.classList.remove("active-burger"),r.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function revievsSlider(){if(!document.querySelector(".revievs"))return null;new Swiper(".revievs__slider",{slidesPerView:1.5,spaceBetween:7,pagination:{el:".revievs__slider-pagination"},breakpoints:{320:{slidesPerView:1.5,spaceBetween:7},767:{slidesPerView:2.5,spaceBetween:10},992:{slidesPerView:3.5,spaceBetween:14}}})}function heroSlider(){if(!document.querySelector(".hero"))return null;new Swiper(".hero__slider",{slidesPerView:1,loop:!0,autoplay:{delay:2500,disableOnInteraction:!1},pagination:{el:".hero__slider-pagination"},navigation:{nextEl:".hero__slider--next",prevEl:".hero__slider--prev"}})}burgerMenu(),window.addEventListener("scroll",fixedNav),revievsSlider(),heroSlider();